BEGIN;
CREATE TABLE IF NOT EXISTS "events"
(
    "id"   bigint GENERATED BY DEFAULT AS IDENTITY NOT NULL,
    "name" varchar                                 NOT NULL,
    PRIMARY KEY ("id")
);
CREATE TABLE IF NOT EXISTS "event_rsvps"
(
    "id"             bigint GENERATED BY DEFAULT AS IDENTITY NOT NULL,
    "event_rsvps"    bigint                                  NULL,
    "invitee_events" bigint                                  NULL,
    PRIMARY KEY ("id")
);
ALTER TABLE "event_rsvps"
    ADD CONSTRAINT "event_rsvps_events_rsvps" FOREIGN KEY ("event_rsvps") REFERENCES "events" ("id") ON DELETE SET NULL,
    ADD CONSTRAINT "event_rsvps_invitees_events" FOREIGN KEY ("invitee_events") REFERENCES "invitees" ("id") ON DELETE SET NULL;
COMMIT;
